import { Response, NextFunction } from 'express';
import { db } from '../db';
import { adminConfigs } from '../db/schema';
import { eq } from 'drizzle-orm';
import { AuthRequest } from '../types';
import { NotFoundError } from '../utils/error';

export class AdminController {
  async getConfig(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const { key } = req.params;

      const [config] = await db
        .select()
        .from(adminConfigs)
        .where(eq(adminConfigs.key, key))
        .limit(1);

      if (!config) {
        throw new NotFoundError(`Config key '${key}' not found`);
      }

      res.json({
        success: true,
        data: config,
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }

  async getAllConfigs(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const configs = await db.select().from(adminConfigs);

      res.json({
        success: true,
        data: configs,
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }

  async updateConfig(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const { key } = req.params;
      const { value, description } = req.body;

      const [updated] = await db
        .update(adminConfigs)
        .set({ value, description, updatedAt: new Date() })
        .where(eq(adminConfigs.key, key))
        .returning();

      if (!updated) {
        const [created] = await db
          .insert(adminConfigs)
          .values({ key, value, description })
          .returning();

        return res.status(201).json({
          success: true,
          data: created,
          message: 'Config created',
          requestId: req.requestId,
        });
      }

      res.json({
        success: true,
        data: updated,
        message: 'Config updated',
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }
}

