import { Response, NextFunction } from 'express';
import { db } from '../db';
import { users } from '../db/schema';
import { eq, and, isNull } from 'drizzle-orm';
import { authService } from '../services/auth';
import { storageService } from '../services/storage';
import { AuthRequest } from '../types';
import { ConflictError, UnauthorizedError, ValidationError } from '../utils/error';

export class AuthController {
  async register(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const { name, email, password, address, phone } = req.body;

      const [existingUser] = await db
        .select()
        .from(users)
        .where(and(eq(users.email, email), isNull(users.deletedAt)))
        .limit(1);

      if (existingUser) {
        throw new ConflictError('Email already registered');
      }

      const passwordHash = await authService.hashPassword(password);

      const [user] = await db
        .insert(users)
        .values({
          name,
          email,
          passwordHash,
          address: address || null,
          phone: phone || null,
          role: 'user',
          status: 'active',
        })
        .returning({
          id: users.id,
          name: users.name,
          email: users.email,
          role: users.role,
        });

      const token = authService.generateToken({
        userId: user.id,
        email: user.email,
        role: user.role,
      });

      res.status(201).json({
        success: true,
        data: { user, token },
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }

  async login(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const { email, password } = req.body;

      const [user] = await db
        .select()
        .from(users)
        .where(and(eq(users.email, email), isNull(users.deletedAt)))
        .limit(1);

      if (!user) {
        throw new UnauthorizedError('Invalid email or password');
      }

      if (user.status === 'suspended') {
        throw new UnauthorizedError('Your account has been suspended');
      }

      if (user.status !== 'active') {
        throw new UnauthorizedError('Your account is not active');
      }

      const isPasswordValid = await authService.comparePassword(password, user.passwordHash);

      if (!isPasswordValid) {
        throw new UnauthorizedError('Invalid email or password');
      }

      const token = authService.generateToken({
        userId: user.id,
        email: user.email,
        role: user.role,
      });

      res.json({
        success: true,
        data: {
          user: {
            id: user.id,
            name: user.name,
            email: user.email,
            role: user.role,
            balance: user.balance,
          },
          token,
        },
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }

  async getProfile(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const [user] = await db
        .select({
          id: users.id,
          name: users.name,
          email: users.email,
          role: users.role,
          status: users.status,
          address: users.address,
          phone: users.phone,
          balance: users.balance,
          verificationStatus: users.verificationStatus,
          createdAt: users.createdAt,
        })
        .from(users)
        .where(eq(users.id, req.user!.id))
        .limit(1);

      res.json({
        success: true,
        data: user,
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }
}

src/controllers/cart.controller.ts.content:
import { Response, NextFunction } from 'express';
import { db } from '../db';
import { cartItems, items } from '../db/schema';
import { eq, and, isNull } from 'drizzle-orm';
import { AuthRequest } from '../types';
import { NotFoundError, ValidationError, ConflictError } from '../utils/error';

export class CartController {
  async addItem(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const { itemId, quantity, type } = req.body;

      const [item] = await db
        .select()
        .from(items)
        .where(and(eq(items.id, itemId), isNull(items.deletedAt)))
        .limit(1);

      if (!item) {
        throw new NotFoundError('Item not found');
      }

      if (item.status !== 'available') {
        throw new ValidationError('Item is not available');
      }

      if (type === 'buy' && item.availability === 'rent_only') {
        throw new ValidationError('This item is only available for rent');
      }

      if (type === 'rent' && item.availability === 'sell_only') {
        throw new ValidationError('This item is only available for sale');
      }

      const [existing] = await db
        .select()
        .from(cartItems)
        .where(
          and(
            eq(cartItems.userId, req.user!.id),
            eq(cartItems.itemId, itemId),
            eq(cartItems.type, type)
          )
        )
        .limit(1);

      if (existing) {
        const [updated] = await db
          .update(cartItems)
          .set({ quantity: existing.quantity + quantity })
          .where(eq(cartItems.id, existing.id))
          .returning();

        return res.json({
          success: true,
          data: updated,
          message: 'Cart item quantity updated',
          requestId: req.requestId,
        });
      }

      const [cartItem] = await db
        .insert(cartItems)
        .values({
          userId: req.user!.id,
          itemId,
          quantity,
          type,
        })
        .returning();

      res.status(201).json({
        success: true,
        data: cartItem,
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }

  async getCart(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const cart = await db
        .select({
          id: cartItems.id,
          quantity: cartItems.quantity,
          type: cartItems.type,
          negotiatedPrice: cartItems.negotiatedPrice,
          negotiatedExpiresAt: cartItems.negotiatedExpiresAt,
          addedAt: cartItems.addedAt,
          item: {
            id: items.id,
            name: items.description,
            sellPrice: items.sellPrice,
            rentPrice: items.rentPrice,
            images: items.images,
            status: items.status,
            availability: items.availability,
          },
        })
        .from(cartItems)
        .leftJoin(items, eq(cartItems.itemId, items.id))
        .where(eq(cartItems.userId, req.user!.id));

      res.json({
        success: true,
        data: cart,
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }

  async updateItem(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const { id } = req.params;
      const { quantity } = req.body;

      const [updated] = await db
        .update(cartItems)
        .set({ quantity })
        .where(and(eq(cartItems.id, parseInt(id)), eq(cartItems.userId, req.user!.id)))
        .returning();

      if (!updated) {
        throw new NotFoundError('Cart item not found');
      }

      res.json({
        success: true,
        data: updated,
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }

  async removeItem(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      const { id } = req.params;

      const [deleted] = await db
        .delete(cartItems)
        .where(and(eq(cartItems.id, parseInt(id)), eq(cartItems.userId, req.user!.id)))
        .returning();

      if (!deleted) {
        throw new NotFoundError('Cart item not found');
      }

      res.json({
        success: true,
        message: 'Item removed from cart',
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }

  async clearCart(req: AuthRequest, res: Response, next: NextFunction) {
    try {
      await db.delete(cartItems).where(eq(cartItems.userId, req.user!.id));

      res.json({
        success: true,
        message: 'Cart cleared successfully',
        requestId: req.requestId,
      });
    } catch (error) {
      next(error);
    }
  }
}

